import React, {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import axios from 'axios';
import {
    saveProduct,
    listProducts,
    deleteProdcut,
} from '../actions/productActions';

function ProductsScreen(props) {
    const [modalVisible, setModalVisible] = useState(false);
    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');
    const [brand, setBrand] = useState('');
    const [category, setCategory] = useState('');
    const [countInStock, setCountInStock] = useState('');
    const [description, setDescription] = useState('');
    const [width, setWidth] = useState('');
    const [height, setHeight] = useState('');
    const [diameter, setDiameter] = useState('');
    const [season, setSeason] = useState('');
    const [manufacturer, setManufacturer] = useState('');
    const [key, setKey] = useState('');
    const [xmlApiMsg, setXmlApiMsg] = useState('');
    const [xmlApiStatus, setXmlApiStatus] = useState('');
    const [uploading, setUploading] = useState(false);
    const productList = useSelector((state) => state.productList);
    const {loading, products, error} = productList;

    const productSave = useSelector((state) => state.productSave);
    const {
        loading: loadingSave,
        success: successSave,
        error: errorSave,
    } = productSave;

    const productDelete = useSelector((state) => state.productDelete);
    const {
        loading: loadingDelete,
        success: successDelete,
        error: errorDelete,
    } = productDelete;
    const dispatch = useDispatch();

    useEffect(() => {
        if (successSave) {
            setModalVisible(false);
        }
        dispatch(listProducts());
        return () => {
            //
        };
    }, [successSave, successDelete]);

    const openModal = (product) => {
        // setModalVisible(true);
        setId(product._id);
        setKey(product.Sifra);
        setName(product.Naziv);
        setPrice(product.Cena);
        setDescription(product.Specifikacija);
        setImage(product.Slika);
        setManufacturer(product.Proizvodjac);
        setCategory(product.Kategorija);
        setCountInStock(product.Lager);
        setSeason(product.Sezona);
        setWidth(product.Sirina);
        setHeight(product.Visina);
        setDiameter(product.Precnik);
    };
    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(
            saveProduct({
                _id: id,
                manufacturer,
                width,
                height,
                diameter,
                season,
                key,
                name,
                price,
                image,
                brand,
                category,
                countInStock,
                description,
            })
        );
    };
    const deleteHandler = (product) => {
        dispatch(deleteProdcut(product._id));
    };
    const uploadFileHandler = (e) => {
        const file = e.target.files[0];
        const bodyFormData = new FormData();
        bodyFormData.append('image', file);
        console.log(bodyFormData);
        setUploading(true);
        axios
            .post('/api/uploads', bodyFormData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            })
            .then((response) => {
                setImage(response.data);
                setUploading(false);
            })
            .catch((err) => {
                console.log(err);
                setUploading(false);
            });
    };
    const uploadXmlFileHandler = (e) => {
        const file = e.target.files[0];
        const bodyFormData = new FormData();
        bodyFormData.append('xml', file);
        setUploading(true);
        axios
            .post('/api/xml', bodyFormData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            })
            .then((response) => {
                setXmlApiMsg(response.data.message);
                setXmlApiStatus(response.status);
                setUploading(false);
                var closeEl = document.querySelector('.-xml-close');
                setTimeout(() => {
                    dispatch(listProducts());
                    closeEl.click();
                }, 3000)
            })
            .catch((err) => {
                setXmlApiMsg(err.response.data.message);
                setXmlApiStatus(err.response.status);
                console.log('error on XML upload', err.response);
                setUploading(false);
            }).finally(() => {

        });
    }
    return (
        <div className="content content-margined">
            <div className="product-header mb-4">
                <h2>Proizovdi</h2>
                <div>
                    <button type="button" className="btn btn-primary mx-3" data-toggle="modal" data-target="#exampleModal" onClick={openModal}>
                        Kreiraj proizovd
                    </button>
                    <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal1">
                        Ubaci XML
                    </button>
                </div>

            </div>

            <div className="modal fade bd-example-modal-lg" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h2 className="modal-title" id="exampleModalLabel">Kreiraj proizvod</h2>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <div className="form-modal">
                                <form onSubmit={submitHandler}>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <label htmlFor="name">Sifra</label>
                                            <input
                                                type="text"
                                                name="name"
                                                value={key}
                                                id="name"
                                                onChange={(e) => setKey(e.target.value)}
                                            ></input>
                                            <label htmlFor="name">Ime</label>
                                            <input
                                                type="text"
                                                name="name"
                                                value={name}
                                                id="name"
                                                onChange={(e) => setName(e.target.value)}
                                            ></input>
                                            <label htmlFor="price">Cena</label>
                                            <input
                                                type="text"
                                                name="price"
                                                value={price}
                                                id="price"
                                                onChange={(e) => setPrice(e.target.value)}
                                            ></input>
                                            <label htmlFor="image">Slika</label>
                                            <input
                                                type="text"
                                                name="image"
                                                value={image}
                                                id="image"
                                                onChange={(e) => setImage(e.target.value)}
                                            ></input>
                                            <input type="file"
                                                   className="w-100"
                                                   onChange={uploadFileHandler}></input>
                                            {uploading && <div>Uploading...</div>}
                                            <label htmlFor="brand">Kategorija</label>
                                            <input
                                                type="text"
                                                name="brand"
                                                value={category}
                                                id="brand"
                                                onChange={(e) => setCategory(e.target.value)}
                                            ></input>

                                            <label htmlFor="brand">Proizvodjac</label>
                                            <input
                                                type="text"
                                                name="brand"
                                                value={manufacturer}
                                                id="brand"
                                                onChange={(e) => setManufacturer(e.target.value)}
                                            ></input>
                                        </div>
                                        <div className="col-md-6">

                                            <label htmlFor="countInStock">Sirina</label>
                                            <input
                                                type="text"
                                                name="countInStock"
                                                value={width}
                                                id="countInStock"
                                                onChange={(e) => setWidth(e.target.value)}
                                            ></input>
                                            <label htmlFor="countInStock">Visina</label>
                                            <input
                                                type="text"
                                                name="countInStock"
                                                value={height}
                                                id="countInStock"
                                                onChange={(e) => setHeight(e.target.value)}
                                            ></input>
                                            <label htmlFor="countInStock">Precnik</label>
                                            <input
                                                type="text"
                                                name="countInStock"
                                                value={diameter}
                                                id="countInStock"
                                                onChange={(e) => setDiameter(e.target.value)}
                                            ></input>
                                            <label htmlFor="countInStock">Broj raspolozivih jedinica</label>
                                            <input
                                                type="text"
                                                name="countInStock"
                                                value={countInStock}
                                                id="countInStock"
                                                onChange={(e) => setCountInStock(e.target.value)}
                                            ></input>
                                            <label htmlFor="name">Sezona</label>
                                            <input
                                                type="text"
                                                name="category"
                                                value={season}
                                                id="category"
                                                onChange={(e) => setSeason(e.target.value)}
                                            ></input>
                                            <label htmlFor="description">Opis</label>
                                            <textarea
                                                name="description"
                                                value={description}
                                                id="description"
                                                className="w-100"
                                                rows={6}
                                                onChange={(e) => setDescription(e.target.value)}
                                            ></textarea>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" id="close-modal-btn" data-dismiss="modal">Odustani</button>
                            <button type="submit" className="btn btn-primary" onClick={submitHandler}>
                                {id ? 'Izmeni' : 'Kreiraj'}
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="modal fade bd-example-modal-lg" id="exampleModal1" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h2 className="modal-title" id="exampleModalLabel">Kreiraj kategoriju</h2>
                            <button type="button" className="close -xml-close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            <div className="form-modal">
                                <form onSubmit={submitHandler}>
                                    <div className="row">
                                        <div className="col-md-12">


                                            <label htmlFor="image">XML file</label>
                                            <input type="file"
                                                   className="w-100"
                                                   onChange={uploadXmlFileHandler}/>
                                            {uploading && <div>Uploading...</div>}
                                        </div>
                                        {xmlApiMsg && <div className="col-md-12 text-white mt-3">
                                            <p className={xmlApiStatus > 250 ? 'bg-danger p-3' : 'bg-success p-3'}>
                                                STATUS ubacivanja XML fajla: {xmlApiMsg}
                                            </p>
                                        </div>}
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" id="close-modal-btn" data-dismiss="modal">Zatvori</button>
                        </div>
                    </div>
                </div>
            </div>

            {modalVisible && (
                <div className="form">
                    <form onSubmit={submitHandler}>
                        <ul className="form-container">
                            <li>
                                <h2>Kreiraj Proizovd</h2>
                            </li>
                            <li>
                                {loadingSave && <div>Loading...</div>}
                                {errorSave && <div>{errorSave}</div>}
                            </li>

                            <li>
                                <label htmlFor="name">Ime</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={name}
                                    id="name"
                                    onChange={(e) => setName(e.target.value)}
                                ></input>
                            </li>
                            <li>
                                <label htmlFor="price">Cena</label>
                                <input
                                    type="text"
                                    name="price"
                                    value={price}
                                    id="price"
                                    onChange={(e) => setPrice(e.target.value)}
                                ></input>
                            </li>
                            <li>
                                <label htmlFor="image">Slika</label>
                                <input
                                    type="text"
                                    name="image"
                                    value={image}
                                    id="image"
                                    onChange={(e) => setImage(e.target.value)}
                                ></input>
                                <input type="file" onChange={uploadFileHandler}></input>
                                {uploading && <div>Uploading...</div>}
                            </li>
                            <li>
                                <label htmlFor="brand">Brend</label>
                                <input
                                    type="text"
                                    name="brand"
                                    value={brand}
                                    id="brand"
                                    onChange={(e) => setBrand(e.target.value)}
                                ></input>
                            </li>
                            <li>
                                <label htmlFor="countInStock">Broj raspolozivih jedinica</label>
                                <input
                                    type="text"
                                    name="countInStock"
                                    value={countInStock}
                                    id="countInStock"
                                    onChange={(e) => setCountInStock(e.target.value)}
                                ></input>
                            </li>
                            <li>
                                <label htmlFor="name">Kategorija</label>
                                <input
                                    type="text"
                                    name="category"
                                    value={category}
                                    id="category"
                                    onChange={(e) => setCategory(e.target.value)}
                                ></input>
                            </li>
                            <li>
                                <label htmlFor="description">Opis</label>
                                <textarea
                                    name="description"
                                    value={description}
                                    id="description"
                                    onChange={(e) => setDescription(e.target.value)}
                                ></textarea>
                            </li>
                            <li>
                                <button type="submit" className="button primary">
                                    {id ? 'Izmeni' : 'Kreiraj'}
                                </button>
                            </li>
                            <li>
                                <button
                                    type="button"
                                    onClick={() => setModalVisible(false)}
                                    className="button secondary"
                                >
                                    Nazad
                                </button>
                            </li>
                        </ul>
                    </form>
                </div>
            )}

            <div className="product-list">
                <table className="table">
                    <thead>
                    <tr>
                        <th>Sifra</th>
                        <th>Ime</th>
                        <th>Cena</th>
                        <th>Kategorija</th>
                        <th>Proizvodjac</th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>
                    {products.map((product) => (
                        <tr key={product._id}>
                            <td className="align-center">{product.Sifra}</td>
                            <td>{product.Naziv}</td>
                            <td>{product.Cena}</td>
                            <td>{product.Kategorija}</td>
                            <td>{product.Proizvodjac}</td>
                            <td>
                                <button type="button" data-toggle="modal" data-target="#exampleModal" className="button" onClick={() => openModal(product)}>
                                    Edit
                                </button>
                                {' '}
                                <button
                                    className="button"
                                    onClick={() => deleteHandler(product)}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ProductsScreen;
