import React, {useEffect, useState} from 'react';
import { addToCart, removeFromCart } from '../actions/cartActions';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import Axios from "axios";
import {CATEGORY_SAVE_FAIL, CATEGORY_SAVE_SUCCESS} from "../constants/categoryConstants";
import {listCategories} from "../actions/categoryAction";
function CartScreen(props) {

  const cart = useSelector(state => state.cart);
  const { cartItems } = cart;
  const productId = props.match.params.id;
  const qty = props.location.search ? Number(props.location.search.split("=")[1]) : 1;
  const dispatch = useDispatch();
  const [name, setName] = useState('');
  const [sureName, setSureName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const removeFromCartHandler = (productId) => {
    dispatch(removeFromCart(productId));
  }
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
    console.log(cartItems);
  }, []);

  const checkoutHandler = () => {
    if (!name || !sureName || !email || !address || !city) {
      return 'validacija'
    }
    let orderBody = {};
    Axios.post(`/api/orders/submit`, orderBody)
        .then(data => {
          console.log(data);
        })
        .catch(error => {
          console.log(error);
        })
        .finally(() => {
          console.log('finally order submitted');
        });
  }

  return <div className="cart">
    <div className="cart-list">
      <ul className="cart-list-container">
        <li>
          <h3>
            Korpa
          </h3>
          <div>
            Cena
          </div>
        </li>
        {
          cartItems.length === 0 ?
            <div>
              Korpa je prazna
          </div>
            :
            cartItems.map((item, id) =>
              <li key={id}>
                <div className="cart-image">
                  <img src={item.Slika} alt="product" />
                </div>
                <div className="cart-name mb-3">
                  <div>
                    <Link to={"/product/" + item._id}>
                      {item.Naziv}
                    </Link>

                  </div>
                  <div>
                    Broj proizvoda:
                  <select className="mr-3 my-3" value={item.qty} onChange={(e) => dispatch(addToCart(item.product, e.target.value))}>
                      {[...Array(item.Lager).keys()].map(x =>
                        <option key={x + 1} value={x + 1}>{x + 1}</option>
                      )}
                    </select>

                    <button type="button" className="button" onClick={() => removeFromCartHandler(item._id)} >
                      Izbrisi
                    </button>
                  </div>
                </div>
                <div className="cart-price">
                  {item.Cena} rsd
                </div>
              </li>
            )
        }
      </ul>

    </div>
    <div className="cart-action">
      <h3>
        Ukupno ({cartItems.reduce((a, c) => a + c.qty, 0)} proizvoda):
        <span className="float-right">
          {cartItems.reduce((a, c) => a + c.Cena * c.qty, 0).toFixed(2)} rsd
        </span>
      </h3>
      <p className="mt-5 info-text">Potrebno je popuniti sva polja kako biste narucili proizvode.</p>
      <div className="form-modal">
          {/*<label htmlFor="name">Ime</label>*/}
          <input
              placeholder="Ime..."
              type="text"
              name="name"
              value={name}
              id="name"
              onChange={(e) => setName(e.target.value)}
          />
          {/*<label htmlFor="sureName">Prezime</label>*/}
          <input
              placeholder="Prezime..."
              type="text"
              name="sureName"
              value={sureName}
              id="sureName"
              onChange={(e) => setSureName(e.target.value)}
          />
        {/*<label htmlFor="phone">Broj telefona</label>*/}
        <input
            placeholder="Broj telefona..."
            type="text"
            name="phone"
            value={phone}
            id="phone"
            onChange={(e) => setPhone(e.target.value)}
        />
          {/*<label htmlFor="address">Adresa</label>*/}
          <input
              placeholder="Adresa..."
              type="text"
              name="address"
              value={address}
              id="address"
              onChange={(e) => setAddress(e.target.value)}
          />
          {/*<label htmlFor="city">Grad</label>*/}
          <input
              placeholder="Grad..."
              type="text"
              name="city"
              value={city}
              id="city"
              onChange={(e) => setCity(e.target.value)}
          />
          {/*<label htmlFor="email">E mail</label>*/}
          <input
              placeholder="E-mail..."
              type="mail"
              name="email"
              value={email}
              id="email"
              onChange={(e) => setEmail(e.target.value)}
          />

      </div>
      <button onClick={checkoutHandler} className="button primary full-width mt-5" disabled={cartItems.length === 0}>
        Odradi Kupovinu
      </button>

    </div>

  </div>
}

export default CartScreen;
